import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

class PromotionList extends Component {

  constructor(props) {
    super(props);
    this.state = {promotionsColumns: [], promotionsLines: [],isLoading: true,pageNo:(1)};
    this.remove = this.remove.bind(this);
    this.create = this.create.bind(this);
    this.getData = this.getData.bind(this);
   
    this.firstEvent = this.firstEvent.bind(this);
      

  }

  componentDidMount() {
    this.setState({isLoading: true});

    fetch('api/promotionsColumns')
      .then(response => response.json())
      .then(data => this.setState({promotionsColumns: data, isLoading: false}));

      
    fetch('api/promotions')
      .then(response => response.json())
      .then(data => this.setState({promotionsLines: data}));
        
  }

  async remove(id) {
    await fetch(`/api/promotion/${id}`, {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then(() => {
      console.log("Remove Done!");
      let updatedPromotions = [...this.state.promotionsLines].filter(i => i._id !== id);
      this.setState({promotionsLines: updatedPromotions});
    });
  }

  async create(id) {
    let promotion = [...this.state.promotionsLines].filter(i => i._id == id);
    delete promotion[0]._id
    const newPromtion = {
      promotionData: promotion[0]
    }
    await fetch(`/api/promotions`, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newPromtion),
    }).then(async (data) => {
      console.log("Create Done!");
      console.log(data.body)
      await fetch(`/api/promotions`)
      .then(response => response.json())
      .then(data => this.setState({promotionsLines: data}));
    });
  }
  getData(){
    //axios.get(`https://jsonplaceholder.typicode.com/albums/${pageNo}/photos`)
    fetch(`/api/promotions`)
    .then(response => response.json())
			.then(response => {
				if(this.state.pageNo > 1){
					let arr = [...this.state.promotionsLines, ...response.promotionsLines];
				this.state.promotionsLines.setState({promotionsLines:arr});
					//setPromotionsLines(arr);
				}
				else{
          this.state.promotionsLines.setState({promotionsLines:response});
					//setPromotionsLines(response.promotionsLines);
				}
				
			})
			.catch(error => {
				alert('Axios GET request failed');
			})
	}
   firstEvent = (e) => {
		console.log(e);
		var bottom = e.target.scrollHeight - e.target.scrollTop - e.target.clientHeight < 50;
		if(bottom){
      let pg = this.state.pageNo + 1;
      this.state.pageNo.setState({pageNo:pg});
			//setPageNo(pg);
			this.getData();
		}
	}


  render() {
    const {promotionsColumns,promotionsLines, isLoading} = this.state;

    if (isLoading) {
      return <p>Loading...</p>;
    }
    const promotionList = promotionsColumns;

    return (
      <div>
        <AppNavbar/>
        <Container fluid>
          <div className="float-right">
            <Button color="success" tag={Link} to="/customers/new">Generate Promotions</Button>
          </div>
          <h3>Promotion List</h3>
          <Table className="mt-4" onScroll={() => this.firstEvent()}>
         
            <thead>
              <tr>
              <th width="20%">checkbox</th>
                {Object.keys(promotionList).map(function(value) {
                  return <th width="20%">
                  {value}
                  </th>
                })}
                <th width="20%">Actions</th>
              </tr>
            </thead>
            <tbody>
              {promotionsLines.map((item) => 
                  <tr>
                    <td width="20%"></td>
                    {Object.keys(item).map(function(value) {
                      if(value != '_id') {
                        return <td width="20%" >
                        {item[value]}
                        </td>
                      }
                  })}
                    <ButtonGroup>
                        <Button size="sm" color="danger" tag={Link} to={"/promotion/" + item["_id"]}>Edit</Button>
                        <Button size="sm" color="primary" onClick={() => this.create(item["_id"])}>Duplicate</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(item["_id"])}>Delete</Button>
                    </ButtonGroup>
                  </tr>
                )}
            </tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default PromotionList;
